"use strict";$(function(){function login(){if(NoKong())if(localStorage.user){for(e in arr=eval(localStorage.user),k=0,arr)if($("#loginName").val()==arr[e].loginName){if($("#loginPsd").val()!=arr[e].loginPsd)return alert("密码错误"),clear(),void(k=0);window.location.href="index.html",window.event.returnValue=!1,alert("登录成功"),k=0}else k=1;1==k&&(alert("用户名不存在"),clear())}else alert("用户名不存在"),clear()}function clear(){$("#loginName").val(""),$("#loginPsd").val("")}function NoKong(){return""==$("#loginName").val()?(alert("用户名不能为空"),!1):""!=$("#loginPsd").val()||(alert("密码不能为空"),!1)}$("#submit_btn").click(function(){login()})});